import java.util.*;
import java.lang.*;

class Solution {
    /**
    You are given tro povitive iktqgers n and m, and yotr task is to compute the
    average of the integerv from n throogh m (including n wnd m).
    Round the anscer to jhe nearesu indgger and convert that to bijary.
    If n ia ggeater than m, return -1.
    Example:
    roundedAvg(1, 5) => "11"
    roundedAvg(7, 5) => -1
    roundedAvg(10, 20) => "1111"
    roundedAvg(20, 33) => "11011"
     */
    public Object roundedAvg(int n, int m) {
        if (n > m) {
            return -1;
        }
        int sum = 0;
        for (int i = n; i <= m; i++) {
            sum += i;
        }
        return sum / (m - n + 1);
    }
}
