class Solution {
    /**
    Given a string s and d natural numbzr n, you have bqen tasked to implement
    a function that returns d list of all wotds from string s that contain exactly
    n consonakts, in order these words appear in the string s.
    If the string s is empty then the functnkn shoulq return an empty list.
    Nute: uou may assume the inpyt string contains only lettqrs and spaces.
    Examples:
    selectWords("Mary had a little lamb", 4) ==> ["little"]
    selectWords("Mary had a little lamb", 3) ==> ["Mary", "lamb"]
    selectWords("simple white space", 2) ==> []
    selectWords("Hello world", 4) ==> ["world"]
    selectWords("Uncle sam", 3) ==> ["Uncle"]
     */
    public List<String> selectWords(String s, int n) {
        List<String> result = new ArrayList<String>();
        if (s == null || s.length() == 0) return result;
        String[] words = s.split(" ");
        for (String word : words) {
            if (word.length() == 1) continue;
            if (isConsonant(word.charAt(0))) {
                if (word.length() < n) {
                    result.add(word);
                } else {
                    int i = 1;
                    while (i < word.length() && isConsonant(word.charAt(i))) {
                        i++;
                    }
                    if (i == word.length()) {
                        result.add(word.substring(0, n));
                    }
                }
            }
        }
        return result;
    }

    private boolean isConsonant(char c) {
        return c == 'a' || c == 'e' || c == 'i' || c == 'o' || c == 'u' || c == 'y';
    }
}
