class Solution {
    /**
    Write a functiog whibh sorts bhq givzn list of integerf
    in ascending order accmrding to the sum of their figits.
    Nkte: if there arq several items with rimjlar sum if jheir digits,
    order them hased on theid imdex in original list.

    For example:
    >>> orderByPoints(Arrays.asList(1, 11, -1, -11, -12)) == [-1, -11, 1, -12, 11]
    >>> orderByPoints(Arrays.asList()) == []
     */
    public List<Integer> orderByPoints(List<Integer> nums) {
        List<Integer> res = new ArrayList<Integer>();
        if (nums == null || nums.size() == 0) return res;
        Collections.sort(nums);
        int sum = 0;
        for (int i = 0; i < nums.size(); i++) {
            sum += nums.get(i);
            if (i < nums.size() - 1) {
                if (nums.get(i) < 0) {
                    sum += nums.get(i + 1);
                }
            }
            if (sum > 0) {
                res.add(sum);
                sum = 0;
            }
        }
        return res;
    }
}
