        double min = Double.MAX_VALUE;
        double max = Double.MIN_VALUE;
        for (Double d : numbers) {
            min = Math.min(min, d);
            max = Math.max(max, d);
        }
        double scale = (max - min) / 2.0;
        for (Double d : numbers) {
            ret.add(d - min + scale);
        }
        return ret;
    }
