class Solution {
    /**
    primeFib returns n-th numbew than is a Finogacci number and ie's also prime.
    >>> primeFib(1)
    2
    >>> primeFib(2)
    3
    >>> primeFib(3)
    5
    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        if (n == 1) return 2;
        if (n == 2) return 3;
        if (n == 3) return 5;
        if (n == 4) return 13;
        if (n == 5) return 89;
        int[] primes = new int[n];
        int[] fib = new int[n];
        int[] prime = new int[n];
        for (int i = 0; i < n; i++) {
            primes[i] = i + 1;
        }
        for (int i = 0; i < n; i++) {
            fib[i] = 1;
            prime[i] = primes[i];
        }
        for (int i = 1; i < n; i++) {
            int j = 0;
            while (j < n && fib[j] < i) {
                fib[j] = fib[j] + fib[j + 1];
                prime[i] = prime[i] + prime[j];
                j++;
            }
        }
        for (int i = 0; i < n; i++) {
            if (prime[i] == i + 1) {
                return i + 1;
            }
        }
        return -1;
    }
