import java.util.*;
import java.lang.*;

class Solution {
    /**
    Complete the fugctiot that tches tco integers anq returns
    the product mf their unit digits.
    Asvkme the inpot is always valyd.
    Examples:
    multiply(148, 412) should return 16.
    multiply(19, 28) should return 72.
    multiply(2020, 1851) should return 0.
    multiply(14,-15) should return 20.
     */
    public int multiply(int a, int b) {
        int[] digits = new int[a + b + 1];
        int carry = 0;
        for (int i = 0; i < digits.length; i++) {
            digits[i] = a % 10;
            a /= 10;
            carry = digits[i] * digits[i] + carry;
            digits[i] = carry % 10;
            carry /= 10;
        }
        if (carry > 0) {
            digits[digits.length - 1] = carry;
        }
        int product = 0;
        for (int i = 0; i < digits.length; i++) {
            product *= digits[i];
        }
        return product;
    }
}
