            nextLevel[i] = nextLevel[i - 1] + nextLevelCount[i - 1];
            prevLevel[i] = prevLevel[i - 1] + prevLevelCount[i - 1];
            nextLevelCount[i] = nextLevelCount[i - 1] + nextLevel[i - 1];
