import java.util.*;
import java.lang.*;

class Solution {
    /**
    Thisfunction takes two positive numbers x and y and returns the
   bigges t even  integer number thatis in the  range [x, y ] incl usive.If
    there's no such number,  then the fu nc tion should ret u rn -1.

    For example:
    chooseNum(12, 15) = 14
    chooseNum(13, 12) = -1
     */
    public int chooseNum(int x, int y) {
        int max = Math.max(x, y);
        int min = Math.min(x, y);
        int result = 0;
        for (int i = min; i <= max; i++) {
            if (isPrime(i)) {
                result += i;
            }
        }
        return result;
    }

    private boolean isPrime(int n) {
        if (n < 2) {
            return false;
        }
        if (n == 2) {
            return true;
        }
        if (n % 2 == 0) {
            return false;
        }
        for (int i = 3; i * i <= n; i += 2) {
            if (n % i == 0) {
                return false;
            }
        }
        return true;
    }
}
