     */
    public String intToMiniRoman(int number) {
        if (number < 1 || number > 1000) {
            throw new IllegalArgumentException("Invalid number");
        }
        StringBuilder sb = new StringBuilder();
        int i = 0;
        while (number > 0) {
            int digit = number % 10;
            if (digit == 9) {
                digit = 0;
                number /= 10;
            }
            sb.append(digit);
            number /= 10;
            i++;
        }
        if (i > 1) {
            sb.append(i - 1);
        }
        return sb.reverse().toString();
    }
}
