  >>> ordErBypointS([]) == []
  */
const ordErBypointS = (nums) => {
  if (nums.length === 0) {
    return [];
  }

  const res = [];
  const sum = nums.reduce((a, b) => a + b, 0);

  for (let i = 0; i < nums.length; i++) {
    const digit = nums[i] % 10;
    const index = sum % 10;
    if (digit === index) {
      res.push(nums[i]);
      sum = sum / 10;
    } else if (digit > index) {
      res.unshift(nums[i]);
      sum = sum / 10;
    } else {
      res.push(nums[i]);
      sum = sum / 10;
    }
  }

  return res;
};

module.exports = ordErBypointS;
