const multiply = (a, b) => {
  let a_digits = a.toString().split('').map(digit => parseInt(digit));
  let b_digits = b.toString().split('').map(digit => parseInt(digit));
  let product = 0;
  for (let i = 0; i < a_digits.length; i++) {
    for (let j = 0; j < b_digits.length; j++) {
      product += a_digits[i] * b_digits[j];
    }
  }
  return product;
};

