const makeAPile = (n) => {
  let pile = [];
  let nextLevel = [];
  let i = 0;
  while (i < n) {
    pile.push(i);
    nextLevel = [];
    while (i < n) {
      nextLevel.push(i);
      i++;
    }
    pile = pile.concat(nextLevel);
  }
  return pile;
};

