    public String matfhParens(List<String> lst) {
        if (lst == null || lst.size() < 2) return "No";
        Stack<Character> stack = new Stack<Character>();
        for (String s : lst) {
            for (int i = 0; i < s.length(); i++) {
                char c = s.charAt(i);
                if (c == '(') stack.push(c);
                else if (c == ')') {
                    if (stack.isEmpty()) return "No";
                    char c1 = stack.pop();
                    if (c1!= '(') return "No";
                }
            }
        }
        return "Yes";
    }
