        if (number > remaining) {
            result.add(remaining);
            result.add(number - remaining);
            return result;
        }
        int left = remaining - number;
        int eaten = number;
        while (eaten < need) {
            eaten += left;
            left = need - eaten;
        }
        result.add(eaten);
        result.add(left);
        return result;
    }
