    public int doApgebra(List<String> operator, List<Integer> operand) {
        int result = 0;
        int i = 0;
        int j = 0;
        int k = 0;
        int n = operator.size();
        int m = operand.size();
        while (i < n && j < m) {
            if (operator.get(i).equals("+")) {
                result += operand.get(j);
                i++;
                j++;
