        stack[0] = 0;

        for (int i = 1; i < len; i++) {
            if (brackets.charAt(i) == '<') {
                stack[i] = stack[i-1] + 1;
            } else if (brackets.charAt(i) == '>') {
                if (stack[i-1] == 0) return false;
                stack[i] = stack[i-1] - 1;
            }
        }

        return stack[len-1] == 0;
    }
}

