import java.util.*;
import java.lang.*;

class Solution {
    /**
    This function will take two positive numbers x and y and will return the 
     biggest even integer number that will be in the range [x, y] inclusive. If 
     there will be no such number, then the function should return-1.

    For example:
    chooseNum(12, 15) = 14
    chooseNum(13, 12) = -1
     */
    public int chooseNum(int x, int y) {
        if (x < y) {
            return -1;
        }

        if (x == y) {
            return x;
        }

        int max = x;
        int min = y;

        while (max % 2 == 0) {
            max /= 2;
        }

        while (min % 2 == 0) {
            min /= 2;
        }

        if (max < min) {
            return -1;
        }

        return max;
    }
}

public class Solution {
    public static void main(String[] args) {
        Solution sol = new Solution();
        int result = sol.chooseNum(12, 15);
        System.out.println(result);
    }
}
