     */
    public List<Integer> maximum(List<Integer> arr, int k) {
        List<Integer> res = new ArrayList<Integer>();
        if (arr == null || arr.size() == 0 || k == 0) return res;
        int n = arr.size();
        int[] max = new int[k];
        int[] min = new int[k];
        for (int i = 0; i < k; i++) {
            max[i] = arr.get(i);
            min[i] = arr.get(i);
        }
        for (int i = 0; i < n - k; i++) {
            int max_index = 0;
            int min_index = 0;
            for (int j = 0; j < k; j++) {
