    >>> primeFib(4)
    13
    >>> primeFib(5)
    89
     */
    public int primeFib(int n) {
        int prime = 2;
        int fib = 1;
        int fib_prime = 1;
        while (prime < n) {
            fib_prime = fib + fib_prime;
            if (isPrime(fib_prime)) {
                prime = fib_prime;
            }
            fib = fib_prime;
        }
        return prime;
    }

    public boolean isPrime(int n) {
        if (n < 2) return false;
        if (n == 2) return true;
        if (n % 2 == 0) return false;
        for (int i = 3; i * i <= n; i += 2) {
            if (n % i == 0) return false;
        }
        return true;
    }
}

