        if (grades == null || grades.size() == 0) return result;
        Collections.sort(grades);
        double min = grades.get(0);
        double max = grades.get(grades.size() - 1);
        double diff = max - min;
        int count = grades.size();
        int i = 0;
        while (i < count) {
