     */
    public List<Integer> makeAPile(int n) {
        List<Integer> result = new ArrayList<Integer>();
        int count = 1;
        while (n > 0) {
            if (n % 2 == 0) {
                result.add(count);
                n = n / 2;
            } else {
                result.add(count * 2);
                n = n - 1;
            }
            count++;
        }
        return result;
    }
