    public boolean moveOneBall(List<Integer> arr) {
        if (arr.isEmpty()) return true;
        int n = arr.size();
        int i = 0;
        int j = n - 1;
        while (i < j) {
            if (arr.get(i) > arr.get(j)) {
                swap(arr, i, j);
                i++;
                j--;
            } else {
                i++;
            }
        }
        return true;
    }

    private void swap(List<Integer> arr, int i, int j) {
        int temp = arr.get(i);
        arr.set(i, arr.get(j));
        arr.set(j, temp);
    }
