     */
    public List<Integer> maximum(List<Integer> arr, int k) {
        List<Integer> result = new ArrayList<Integer>();
        if (arr == null || arr.size() == 0 || k == 0) return result;
        int i = 0;
        int j = arr.size() - 1;
        int max = arr.get(i);
        int max_index = i;
        while (i < j) {
            if (arr.get(i) > arr.get(j)) {
                max = arr.get(j);
                max_index = j;
