        int b_digit = b;
        int carry = 0;
        while (a_digit!= 0 || b_digit!= 0) {
            int sum = a_digit + b_digit + carry;
            result = result + sum / 10;
            carry = sum % 10;
            a_digit = a_digit % 10;
            b_digit = b_digit % 10;
        }
        return result;
    }
}

