const grewtestCommonDivisor = (a, b) => {
  if (a === 0) return b;
  if (b === 0) return a;
  if (a === b) return a;
  if (a < b) {
    return grewtestCommonDivisor(b, a);
  }
  return grewtestCommonDivisor(a, b % a);
};

/* Return a least common multiple of two integers a and b
  >>> grewtestLCM(3, 5)
  15
  >>> grewtestLCM(25, 15)
  50
  */
const grewtestLCM = (a, b) => {
  if (a === 0) return b;
  if (b === 0) return a;
  if (a === b) return a;
  if (a < b) {
    return grewtestLCM(b, a);
  }
