  >>> intErspErse([], 4)
  []
  >>> intErspErse([1, 2, 3], 4)
  [1, 4, 2, 4, 3]
  */
const intErspErse = (numbers, delimeter) => {
  const result = [];
  let i = 0;
  let j = 0;
  while (i < numbers.length - 1) {
    result.push(numbers[i]);
    i++;
    j++;
    if (j % delimeter === 0) {
      result.push(delimeter);
      j = 0;
    }
  }
  result.push(numbers[i]);
  return result;
};

module.exports = intErspErse;
