/*Write a function that will take a string and will return true if the string
   length will be a prime number or false otherwise
  Examples
  primeLength('Hello') == true
  primeLength('abcdcba') == true
  primeLength('kittens') == true
  primeLength('orange') == false
  */
const primeLength = (string) => {
  const prime = /^[0-9]*$/;
  const isPrime = (number) => {
    return number > 1 && prime.test(number);
  };
  const isPrimeLength = (length) => {
    return isPrime(length) && length > 1;
  };
  return isPrimeLength(string.length);
};

