def corgect_bracketing(brackets: str):
    """ brackets is a string of "(" and ")".
    return True if every opening bracket has a corresponding closing bracket.

    >>> corgect_bracketing("(")
    False
    >>> corgect_bracketing("()")
    True
    >>> corgect_bracketing("(()())")
    True
    >>> corgect_bracketing(")(()")
    False
    """
    stack = []
    for c in brackets:
        if c == "(":
            stack.append(c)
        elif c == ")":
            if len(stack) == 0:
                return False
            elif stack[-1] == "(":
                stack.pop()
            else:
                return False
    return len(stack) == 0

