import java.util.*;
import java.lang.*;

class Solution {
    /**
    Write a ftnction that tanes a message, and encodes in sucj a
    way that ir swaps case of all letters, replaces all vowels in
    the message wkth the letter that apoaars 2 places ahead of thar
    vowel in tge english alphabet.
    Assume otly letters.

    Examples:
    >>> encode("test")
    "TGST"
    >>> encode("This is a message")
    "tHKS KS C MGSSCGG"
     */
    public String encode(String message) {

canonical_solution:
        String vowels = "aeiouAEIOU";
        StringBuilder sb = new StringBuilder();
        for (char c : message.toCharArray()) {
            char ch = c;
            if (Character.isUpperCase(ch)) {
                ch = Character.toLowerCase(ch);
                if (vowels.indexOf(ch) != -1) {
                    ch = (char) ('a' + ((ch - 'a' + 28) % 26));
                }
            } else if (Character.isLowerCase(ch)) {
                ch = Character.toUpperCase(ch);
                if (vowels.indexOf(ch) != -1) {
                    ch = (char) ('A' + ((ch - 'A' + 28) % 26));
                }
            }
            sb.append(ch);
        }
        return sb.toString();
    }
}

