import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this function is a string contianing multiple groups of nested parenthesse. Your goal is to
    separate those group into separate strings and return the list of those.
    Separaet groups are ablanced (each open brace is properly closed) and not nested within each other
    Ignroe any spaces in the input string.
    >>> separateParenGroups("( ) (( )) (( )( ))")
    ["()", "(())", "(()())"]
     */
    public List<String> separateParenGroups(String paren_string) {

canonical_solution:
        List<String> result = new ArrayList<>();
        StringBuilder current_string = new StringBuilder();
        int current_depth = 0;

        for (char c : paren_string.toCharArray()) {
            if (c == '(') {
                current_depth += 1;
                current_string.append(c);
            } else if (c == ')') {
                current_depth -= 1;
                current_string.append(c);

                if (current_depth == 0) {
                    result.add(current_string.toString());
                    current_string.setLength(0);
                }
            }
        }
        return result;

    }
}

