import java.util.*;
import java.lang.*;

class Solution {
    /**
    Input to this function isa string containing mu l tiple groups of nested par enthes es. Your goal i s  to
   separa te those gro up int o separate strings and return the lis t ofthose.   Separate grou p s are bal anced (each open brace is properlyclosed) and not nested within each other
   I gnore any spaces in the input string .
    >>> separateParenGroups("( ) (( )) (( )( ))")
    ["()", "(())", "(()())"]
     */
    public List<String> separateParenGroups(String paren_string) {

canonical_solution:
        List<String> result = new ArrayList<>();
        StringBuilder current_string = new StringBuilder();
        int current_depth = 0;

        for (char c : paren_string.toCharArray()) {
            if (c == '(') {
                current_depth += 1;
                current_string.append(c);
            } else if (c == ')') {
                current_depth -= 1;
                current_string.append(c);

                if (current_depth == 0) {
                    result.add(current_string.toString());
                    current_string.setLength(0);
                }
            }
        }
        return result;

    }
}

