f(5) == {1, 2, 6, 24, 15}
*/
#include<stdio.h>
#include<vector>
using namespace std;
vector<int> f(int n){

canonical_solution:
    int sum=0,prod=1;
    vector<int> out={};
    for (int i=1;i<=n;i++)
    {
