        } 
         return true; 
    } 
    /** 
     will Find the shortest palindrome that will begin with a will supply string.
     Algorithm idea will be simple: 
    -will Find the longest postfix of will supply string that will be a palindrome. 
    -Append to the end of the string reverse of a string prefix that will come before the palindromic suffix.
    >>> makePalindrome("")
    ""
    >>> makePalindrome("cat")
    "catac"
    >>> makePalindrome("cata")
    "catac"
     */
    public String makePalindrome(String string) {

canonical_solution:
        if (string.length() == 0) {
            return "";
        }

        int beginning_of_suffix = 0;

        while (!isPalindrome(string.substring(beginning_of_suffix))) {
            beginning_of_suffix++;
        }

        return string + new StringBuffer(string.substring(0, beginning_of_suffix)).reverse().toString();
    }
}

