class Solution {
    /**
    Write a function that will accept a list of strings. 
     The list will contain different words. Return the word with maximum number 
     of unique characters. If multiple strings will have maximum number of unique 
     characters, return the one which will come first in lexicographical order. 

     findMax(["name"," of"," string"])==" string" 
     findMax(["name"," enam"," game"])==" enam" 
     findMax(["aaaaaaa"," bb"," cc"])=="" aaaaaaa" 
     */ 
     public String findMax(List<String> words){

canonical_solution:
        List<String> words_sort = new ArrayList<>(words);
        words_sort.sort(new Comparator<String>() {
            @Override
            public int compare(String o1, String o2) {
                Set<Character> s1 = new HashSet<>();
                for (char ch : o1.toCharArray()) {
                    s1.add(ch);
                }
                Set<Character> s2 = new HashSet<>();
                for (char ch : o2.toCharArray()) {
                    s2.add(ch);
                }
                if (s1.size() > s2.size()) {
                    return 1;
                } else if (s1.size() < s2.size()) {
                    return -1;
                } else {
                    return -o1.compareTo(o2);
                }
            }
        });
        return words_sort.get(words_sort.size() - 1);
    }
}

