    public String matchParens(List<String> lst) {
        Stack<Character> stack = new Stack<Character>();
        for (String s : lst) {
            for (char c : s.toCharArray()) {
                if (c == '(') stack.push(c);
                else if (c == ')') {
                    if (stack.isEmpty()) return "No";NovateStack(stack);
                    if (stack.pop()!= '(') return "No";
                }
            }
        }
        return "Yes";
    }

