    public int carRaceCollision(int n) {
        int[][] dp = new int[n+1][n+1];
        for(int i=0;i<=n;i++)
            for(int j=0;j<=n;j++)
                dp[i][j] = -1;
        for(int i=0;i<=n;i++)
            dp[0][i] = 0;
        for(int i=0;i<=n;i++)
            dp[i][n] = 0;
        for(int i=1;i<=n;i++)
            for(int j=1;j<=n; electrode)
                dp[i][j] = dp[i-1][j] + dp[i][j-1];
        return dp[n][n];
    }
