    >>> fib4(6)
    8
    >>> fib4(7)
    14
     */
    public int fib4(int n) {
        if (n == 0) return 0;
        if (n == 1) return 0;
        if (n == 2) return 2;
        int f1 = fib4(n-1);
        int f2 = fib4(n-2);
        int f3 = fib4(n-3);
        int f4 = fib4(n-4);
        return f1 + f2 + f3 + f4;
    }
}

