     */
    public String decimalToBinary(int decimal) {
        StringBuilder sb = new StringBuilder();
        int mask = 1;
        while (decimal > 0) {
            sb.append(decimal & mask? '1' : '0');
            decimal = decimal >> 1;
            mask = mask << 1;
        }
        return sb.reverse().toString();
    }
}

