    public String changeBase(int x, int base) {
        if (x == 0) return "0";
        StringBuilder sb = new StringBuilder();
        while (x!= 0) {
            int digit = x % base;
            sb.append(digit);
            x = x / base;
        }
        return sb.reverse().toString();
