  */
const bf = (planet1, planet2) => {
  const planet1 = planet1.toLowerCase();
  const planet2 = planet2.toLowerCase();
  const distance = (planet1 === planet2)? 0 : Math.abs(Math.abs(planet1) - Math.abs(planet2));
  const planets = [];
  const planetsInRange = [];
  const planetsInRange2 = [];
