  */
const parse_nested_parens = (paren_string) => {
  const stack = [paren_string];
  const level = 0;
  const max_level = 0;
  const result = [];

  while (stack.length > 0) {
    const current_level = stack.length - 1;
    const current_string = stack[current_level];
    const next_string = stack[current_level + 1];

