  [1, 3, 4]
  */
const remove_duplicates = (numbers) => {

canonical_solution:
  var dict = new Object();
  for (const num of numbers) {
    if (num in dict) {
      dict[num] += 1;
    } else {
      dict[num] = 1;
    }
  }
  return numbers.filter(x => dict[x] <= 1);
}

