otherwise keep it as it is.
If the string contains no letters, reverse the string.
The function should return the resulted string.
Examples
solVe("1234") = "4321"
solVe("ab") = "AB"
solVe("#a@C") = "#A@c"
*/
#include<stdio.h>
#include<string>
using namespace std;
string solVe(string s){
	int len = s.length();
	if(len == 0)
		return s;
	int i = 0;
	while(i < len){
		if(s[i] >= 'a' && s[i] <= 'z'){
			s[i] = s[i] - 'a' + 'A';
		}else if(s[i] >= 'A' && s[i] <= 'Z'){
			s[i] = s[i] - 'A' + 'a';
		}
		i++;
	}
	return s;
}
int main(){
	string s = "1234";
	printf("%s\n",solVe(s).c_str());
	return 0;
}
