				output.push_back(paren_string.substr(0, i));
				paren_string = paren_string.substr(i);
				open_paren--;
			}
			else if(paren_string[i] == ')'){
				output.push_back(paren_string.substr(0, i));
				paren_string = paren_string.substr(i);
				close_paren--;
			}
		}
	}
	return output;
}

