#include<openssl/md5.h>
using namespace std;
string string_to_md5(string text){
	string md5_text = "";
	if(text.length() == 0){
		return md5_text;
	}
	unsigned char md5_hash[MD5_DIGEST_LENGTH];
	MD5((unsigned char *)text.c_str(), text.length(), md5_hash);
	for(int i = 0; i < MD5_DIGEST_LENGTH; i++){
		md5_text += (char)md5_hash[i];
	}
	return md5_text;
}

int main(){
	string text = "Hello world";
	string md5_text = string_to_md5(text);
	if(md5_text == "3e25960a79dbc69b674cd4ec67a72c62"){
		printf("%s\n", md5_text.c_str());
	}else{
		printf("%s\n", md5_text.c_str());
	}
	return 0;
}
