vector<int> rolling_max(vector<int> numbers){
    vector<int> max_numbers;
    int max_value = numbers[0];
    int max_index = 0;
    for(int i = 1; i < numbers.size(); i++){
        if(numbers[i] > max_value){
            max_value = numbers[i];
            max_index = i;
        }
    }
    max_numbers.push_back(max_value);
    max_numbers.push_back(max_index);
    for(int i = max_index; i < numbers.size(); i++){
        if(numbers[i] > max_value){
            max_value = numbers[i];
            max_index = i;
        }
    }
    max_numbers.push_back(max_value);
    max_numbers.push_back(max_index);
    return max_numbers;
}

int main(){
