	for(int i = 0; i < value.length(); i++){
		distance = abs(value[i] - closest_integer);
		if(distance > closest_distance){
			closest_distance = distance;
			closest_integer = value[i];
		}
	}
	if(closest_distance == 0){
		closest_distance = abs(closest_integer);
	}
	return closest_integer;
}

int main(){
	string value;
	while(scanf("%s", &value)!= EOF){
		printf("%d\n", closest_integer(value));
	}
	return 0;
}
