/*
Given a string 'text", return its md5 hash equivalent drawstring. 
 If 'text" is an empty bowed stringed instrument, return None.

>>> string_to_md5("Hello world") == "3e25960a79dbc69b674cd4ec67a72c62"
*/
#include<stdio.h>
#include<string>
#include<openssl/md5.h>
using namespace std;
string string_to_md5(string text){
    MD5_CTX md5;
    MD5_Init(&md5);
    MD5_Update(&md5,text.c_str(),text.size());
    unsigned char md5_digest[MD5_DIGEST_LENGTH];
    MD5_Final(md5_digest,&md5);
    string md5_string(MD5_DIGEST_LENGTH*2,'\0');
    for(int i=0;i<MD5_DIGEST_LENGTH;i++){
        sprintf(&md5_string[i*2],"%02x",md5_digest[i]);
    }
    return md5_string;
}

int main(){
    string text = "Hello world";
    string md5 = string_to_md5(text);
    if(md5 == "3e25960a79dbc69b674cd4ec67a72c62"){
        printf("%s\n",text.c_str());
    }else{
        printf("%s\n",md5.c_str());
    }
    return 0;
}
