float truncate_number(float number){
	float integer_part, decimal_part;
	int integer_number;
	float fractional_number;

	integer_part = floor(number);
	fractional_number = number - integer_part;
	decimal_part = fractional_number;

	if(fractional_number < 0.0){
		decimal_part = -decimal_part;
	}

