		decimal_part = -decimal_part;
		integer_part = -integer_part;
	}

	if(decimal_part > 1.0){
		decimal_part = 1.0;
		integer_part = integer_part + 1;
	}

	if(decimal_part < 0.0){
		decimal_part = 0.0;
		integer_part = integer_part - 1;
	}

	return integer_part + decimal_part;
}

